mapper.select:
select g.gdsNum, g.gdsName, g.cateCode, c.cateCodeRef, c.cateName, gdsPrice, gdsStock, gdsDes, gdsImg, gdsDate, g.gdsImg, g.gdsThumbImg from tbl2_goods g
inner join goods2_category c
on g.cateCode = c.cateCode where g.gdsNum = #{gdsNum}

mapper.update:
update tbl2_goods
set gdsName = #{gdsName}, cateCode = #{cateCode}, gdsPrice = #{gdsPrice}, gdsStock = #{gdsStock}, gdsDes = #{gdsDes}, gdsImg = #{gdsImg}, gdsThumbImg =
#{gdsThumbImg}
where gdsNum = #{gdsNum}
